import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = { nome: '', rga: '', alunos: [] };

    this.handleChange = this.handleChange.bind(this);
    this.handleChange2 = this.handleChange2.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    fetchAlunos().then(response => {
      this.setState({
        alunos: response.body
      });
    });
  }


  handleChange(event) {
    this.setState({ nome: event.target.value });
  }

  handleChange2(event) {
    this.setState({ rga: event.target.value });
  }

  handleSubmit(event) {
    alert('Nome: ' + this.state.nome + ' RGA: ' + this.state.rga);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" nome={this.state.nome} onChange={this.handleChange} />
        </label>
        <label>
          RGA:
          <input type="text" rga={this.state.rga} onChange={this.handleChange2} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }

}

export default App;
