  constructor(props) {
    super(props);
    this.state = {nome: ''};
    this.state = {rga: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({value: event.target.nome});
    this.setState({value: event.target.rga});
  }

  handleSubmit(event) {
    alert('Enviado com sucesso! Nome:' + this.state.nome + ' RGA: ' + this.state.rga);
    event.preventDefault();
  }

