        <label>Nome: </label>
        <input type="text" value={this.state.nome} onChange={this.handleChange}/>
        <br />

        <label>RGA: </label>
        <input type="text" value={this.state.rga} onChange={this.handleChange1}/>
        <br />

        <input type="button" value="Submit" />

          <hr />      
      </div>
    );
  }

}

export default App;
