import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {nome: '', rga: 'a'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({nome: event.target.value});
    this.setState({rga: event.target.value});
  }

  handleSubmit(event) {
    alert('Nome: ' + this.state.nome + ' RGA: ' + this.state.rga);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" nome={this.state.value} onChange={this.handleChange} />
        </label>
        <label>
          RGA:
          <input type="text" rga={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }

}

export default App;
