import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {nome: '', rga: ''}
  }

  handleChange(event) {
    this.setState({nome: event.target.nome});
    this.setState({rga: event.target.nome});
  }

  handleSubmit(event) {
    alert('Cadastrado com sucesso!');
    event.preventDefault();
  }

  render() {    
    return (

      <form onSubmit={this.handleChange}>
        <div className="App"> 
        <h2>Cadastro</h2>
          <hr />      
          <label>Nome: </label>
          <input type="text" nome={this.state.nome} />
          <br />
          <label>RGA: </label>
          <input type="text" rga={this.state.rga}/>
          <br />
          <input type="button" value="Submit" />
          <hr />      
        </div>

      </form>

    );
  }

}

export default App;
